import{_ as a,c as i,a2 as o,G as n,B as c,o as r}from"./chunks/framework.DN5Ma_nw.js";const b=JSON.parse('{"title":"宽度适配内容","description":"","frontmatter":{},"headers":[],"relativePath":"CSS/效果提升/宽度适配内容.md","filePath":"learn/CSS/效果提升/宽度适配内容.md","lastUpdated":1733132188000}'),l={name:"CSS/效果提升/宽度适配内容.md"};function d(s,e,p,h,m,g){const t=c("Iframe");return r(),i("div",null,[e[0]||(e[0]=o(`<h1 id="宽度适配内容" tabindex="-1">宽度适配内容 <a class="header-anchor" href="#宽度适配内容" aria-label="Permalink to &quot;宽度适配内容&quot;">​</a></h1><h2 id="效果实现" tabindex="-1">效果实现 <a class="header-anchor" href="#效果实现" aria-label="Permalink to &quot;效果实现&quot;">​</a></h2><p>先看一个案例：</p><p><img src="https://pic.imgdb.cn/item/651fd61bc458853aefb2dcd8.jpg" alt="案例" loading="lazy"></p><p>我们希望蓝色背景盒子宽度为内容的宽度，此时只需要设置其宽度属性。代码如下：</p><div class="language-css vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">css</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">.title</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;"> {</span></span>
<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">    width</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">fit-content</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">;</span></span>
<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">}</span></span></code></pre></div><p>修改后效果如下：</p><p><img src="https://pic.imgdb.cn/item/651fd85dc458853aefb4127d.jpg" alt="修改后的效果" loading="lazy"></p><p>且该方法没有任何兼容问题。MDN 文档解释指路：[fit-content](<a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS/fit-content" target="_blank" rel="noreferrer">fit-content - CSS：层叠样式表 | MDN (mozilla.org)</a>) 。</p><h2 id="区别" tabindex="-1">区别 <a class="header-anchor" href="#区别" aria-label="Permalink to &quot;区别&quot;">​</a></h2><p>CSS width 属性有三种属性：<code>max-content</code> 、<code>min-content</code> 以及 <code>fit-content</code> ，三者的区别如下：</p><ul><li><p>max-content</p><p><code>max-content</code> 尺寸关键字代表了内容的最大宽度或最大高度。对于文本内容而言，这意味着内容即便溢出也不会被换行。</p><p>效果如下：</p><p><img src="https://pic.imgdb.cn/item/651fda4bc458853aefb4cdf1.jpg" alt="max-content" loading="lazy"></p></li><li><p>min-content</p><p><code>min-content</code> 是一个 CSS 的尺寸关键字，表示的是内容的最小宽度。对于文本内容而言，这意味着内容会利用所有软换行的机会，变得尽可能的小，大小不会超过最长单词的宽度。</p><p>效果如下：</p><p><img src="https://pic.imgdb.cn/item/651ff1c8c458853aefc326cd.jpg" alt="min-content效果" loading="lazy"></p></li><li><p>fit-content</p><p><code>fit-content</code> 行为类似于 <code>fit-content(stretch)</code>，实际上这意味着盒子会使用可用的空间，但永远不会超过 <a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS/max-content" target="_blank" rel="noreferrer"><code>max-content</code></a>。</p><p><code>fit-content</code> 属性可以用于设置元素的 <a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS/width" target="_blank" rel="noreferrer"><code>width</code></a>、<a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS/height" target="_blank" rel="noreferrer"><code>height</code></a>、<a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS/min-width" target="_blank" rel="noreferrer"><code>min-width</code></a>、<a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS/min-height" target="_blank" rel="noreferrer"><code>min-height</code></a>、<a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS/max-width" target="_blank" rel="noreferrer"><code>max-width</code></a> 和 <a href="https://developer.mozilla.org/zh-CN/docs/Web/CSS/max-height" target="_blank" rel="noreferrer"><code>max-height</code></a>，此时最大和最小尺寸将基于元素的内容尺寸计算。</p><p>效果如下：</p><p><img src="https://pic.imgdb.cn/item/651ff1f8c458853aefc32c37.jpg" alt="fit-content效果" loading="lazy"></p></li></ul><p><code>max-content</code> 和 <code>fit-content</code> 在自己内容不足以撑满父盒子时，宽度只为自己内容的最大宽度，不会再强行增大。</p><p><code>fit-content</code> 官方给出可以表示为一个公式：</p><p><img src="https://pic.imgdb.cn/item/651ff270c458853aefc3985f.jpg" alt="公式" loading="lazy"></p><h2 id="总体效果" tabindex="-1">总体效果 <a class="header-anchor" href="#总体效果" aria-label="Permalink to &quot;总体效果&quot;">​</a></h2>`,16)),n(t,{url:"https://duyidao.github.io/blogweb/#/detail/css/width"})])}const k=a(l,[["render",d]]);export{b as __pageData,k as default};
